import Image from "next/image";

interface Props {
  album: string
  title: string;
  description: string;
  images: string;
}

export const Album = ({ images, album, title, description }: Props) => {
  const imagesArray = images.split(",").map(num => num.padStart(2, '0'));

  const basePath = `/img/photography/${album}`;
  return (
    <div>


      <div className="pb-8">
        <h2 className="text-8xl font-heading">{title}</h2>
        <div className="pt-2 text-2xl text-neutral-700">{description}</div>
      </div>

      <div className="grid grid-cols-3 gap-4 ">
        {imagesArray.map((image) => (
          <div key={image} className="relative h-132">
            <Image src={`${basePath}/${image}.jpeg`}
              blurDataURL="data:image/svg+xml;base64,"
              placeholder="blur"
              alt={title}
              fill
              className="object-cover object-center" />
            <div className="absolute inset-0 bg-black/10" />
            <div className="absolute bottom-0 left-0 bg-black text-white text-sm p-2">
              {image}
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}